//转换值
function getLevelNumber(value) {
    if (value == "") {
        return 0;
    }
    else if (value == "Ⅰ") {
        return 1;
    }
    else if (value == "Ⅱ") {
        return 2;
    }
    else if (value == "Ⅲ") {
        return 3;
    }
    else if (value == "Ⅳ") {
        return 4;
    }
    else if (value == "Ⅴ") {
        return 5;
    }
    else if (value == "劣Ⅴ") {
        return 6;
    }
    return value;
}

//支流断面信息
function searchZLRiverInfo(riverCode) {
    var stationInfo = [];
    if (riverCode.substring(5) == "000000") {
        for (var i = 0; i < stationInfos.length; i++) {
            if (stationInfos[i].RiverCode) {
                if (stationInfos[i].RiverCode.substring(0, 5) == riverCode.substring(0, 5) && stationInfos[i].RiverCode.substring(5) != "000000") {
                    stationInfo.push(stationInfos[i]);
                }
            }
        }
    } else {
    }
    setTimeout(function () {
        LoadGrid(stationInfo, 2);
    }, 1);
}

