//折线图
function EChart_createAirLine(element, title, category, data) {
    var worldMapContainer = document.getElementById(element);
    //用于使chart自适应高度和宽度,通过窗体高宽计算容器高宽
    var resizeWorldMapContainer = function () {
        worldMapContainer.style.width = (window.innerWidth - 18) + 'px';
        worldMapContainer.style.height = (window.innerHeight - 40) + 'px';
    };
    //设置容器高宽
    resizeWorldMapContainer();
    var myChart = echarts.init(worldMapContainer);

    option = {
        title: {
            text: title,
            x: "center",
            y: "top",
            textStyle: {
                fontSize: 12,
                fontWeight: 'normal'
            },
            padding: 10
        },
        //grid: {
        //    x: 20,
        //    y: 10,
        //    x2: 35,
        //    y2: 40
        //},
        tooltip: {
            trigger: 'axis'
        },
        toolbox: {
            show: false,
            feature: {
                mark: { show: true },
                dataView: { show: true, readOnly: false },
                magicType: { show: true, type: ['line', 'bar'] },
                restore: { show: true },
                saveAsImage: { show: true }
            }
        },
        legend: {
            data: ["水质质量"],
            height: worldMapContainer.style.height,
            width: worldMapContainer.style.width
        },
        grid: {
            left: '3%',
            right: '8%',
            bottom: '5%',
            containLabel: true
        },
        toolbox: {
            feature: {
                saveAsImage: {}
            }
        },
        xAxis: {
            type: 'category',
            boundaryGap: false,
            data: category,
        },
        yAxis: {
            max: 6,
            min: 0,
            type: 'value',
            axisLabel: {
                formatter: '{value}',
                formatter: function (value) {
                    if (value == 0) {
                        return "";
                    }
                    else if (value == 1) {
                        return "Ⅰ";
                    }
                    else if (value == 2) {
                        return "Ⅱ";
                    }
                    else if (value == 3) {
                        return "Ⅲ";
                    }
                    else if (value == 4) {
                        return "Ⅳ";
                    }
                    else if (value == 5) {
                        return "Ⅴ";
                    }
                    else if (value == 6) {
                        return "劣Ⅴ";
                    }
                    return value;
                }
            }
        },
        series: [
            {
                name: '水质质量',
                type: 'line',
                data: data,
            },
        ]
    };
    myChart.setOption(option);
}

